insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.1.1.7.1', 'cae', '2.1.1.7.1', 'Afficher le diagnostic de performance énergétique (DPE) sur des bâtiments non concernés par l''obligation d''affichage', '<p>En plus des obligations règlementaires d''affichage sur la plateforme OPERAT pour les bâtiments tertiaires.</p>
', '', '', '', '', '', null, null, null);
