insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.1.2.3.1', 'cae', '2.1.2.3.1', 'Définir des objectifs de long terme (2050) et court moyen terme (2030) et tracer une trajectoire cible avec les rythmes et performances à atteindre pour la massification de la rénovation énergétique du patrimoine communal (objectifs quantifiés en nombre ou % de bâtiments à rénover, avec un échéancier) sur la base d’un état des lieux initial', '<p>Se référer à la mesure 2.1.1 pour l''état des lieux initial.</p>
<p>Ce rythme et les performances visées sont a minima conformes avec les obligations de rénovation du décret n°2019-771 du 23 juillet 2019 relatif aux obligations d’actions de réduction de la consommation d’énergie finale dans des bâtiments à usage tertiaire.</p>
', '', '', '', '', '', null, null, null);
