insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.1.2.3.4', 'cae', '2.1.2.3.4', 'Viser a minima le niveau BBC-rénovation en Métropole (éventuellement en plusieurs étapes « BBC-compatibles ») et une consommation énergétique inférieure à 300 kWhEP/m².an dans les DOM (correspondant à la classe A du DPE-G pour les bâtiments tertiaires existants sauf santé et hôtels) pour les rénovations', '', '', '', '', '', '', null, null, null);
