insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.1.2.5.4', 'cae', '2.1.2.5.4', 'Instaurer un fonds local d’aide aux travaux via les ressources issues du développement des énergies renouvelables sur le territoire ou fonds d’amorçage gérés par l’EPCI', '<p>Le principe du fonds d''amorçage est que les économies d’énergies réalisées sur les premières opérations permettent de refinancer ce fonds pour accompagner de nouveaux projets de rénovation.</p>
', '', '', '', '', '', null, null, null);
