insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.1.2.6.1', 'cae', '2.1.2.6.1', 'Renforcer le suivi énergétique des bâtiments concernés par le plan (si la compétence gestion et suivi est déléguée, la collectivité suit activement les activités du gestionnaire et dispose d’une vision de la situation existante). Le suivi est a minima annuel pour les bâtiments ciblés par le décret tertiaire.', '', '', '', '', '', '', null, null, null);
