insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.3.3.2', 'cae', '2.2.3.3.2', 'Porter une attention particulière au traitement des fumées des installations biomasse collective, allant au-delà de la réglementation', '<p>Exemples: mise en place des meilleures techniques disponibles – électrofiltre, filtres à manches, régulation, foyer bas-NOx ou systèmes de dé-NOx- notamment en zones sensibles en matière de qualité de l’air, à proximité des écoles, etc.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.3.4', 'cae', '2.2.3.4', 'Suivre et atteindre une part d’énergie renouvelable et de récupération conséquente pour la chaleur et le rafraîchissement des bâtiments publics', '<p>La proportion d''énergies renouvelables et de récupération dans le domaine thermique* atteint plus de 38% des besoins thermiques du patrimoine de la collectivité en Métropole, 75% dans les DOM (valorisation progressive à partir de 0% ; pour rappel, l''autonomie énergétique est l''objectif pour 2030).</p>
<p>Ces taux, lorsque cela est possible, sont notamment atteints grâce au développement des réseaux de chaleur vertueux (mesure 1.2.1 et 3.2.2).</p>
<p>Les installations ne sont prises en compte qu''à partir de leur mise en service.</p>
<p>*Dans les DOM, climatisation et eau chaude sanitaire</p>
', '', '', '', '', '', null, 75.0, 'effets');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2', 'cae', '2.2.2', 'Augmenter l''efficacité énergétique pour l''électricité des bâtiments publics', '<p>La collectivité développe l''efficacité énergétique pour les usages de l''électricité dans ses bâtiments et l''évalue au moyen d''indicateurs énergétiques pour différentes catégories de bâtiments.</p>
', '<p><strong>Contexte et enjeux</strong></p>
<p>D’après l’enquête « Energie et patrimoine communal » le budget énergie d’une commune représente en moyenne 4% de son budget de fonctionnement (1,8% dans les communes des DOM et 1,5% dans les EPCI). C’est un poste conséquent qu’il est possible de réduire en augmentant l’efficacité énergétique pour l’électricité des bâtiments publics.</p>
<p>Avec l''augmentation de la production renouvelable décentralisée, et les recharges de véhicules électriques en particulier dans les immeubles, les bâtiments doivent passer de simples consommateurs d''énergie à des bâtiments acteurs du système énergétique, maîtrisant dynamiquement sa consommation, sa production et son stockage d''énergie.</p>
<p><strong>Réglementation</strong></p>
<p>Loi ELAN, article 175 : pour les bâtiments tertiaires réduction de la conso d''énergie finale de 40% en 2030, 50% en 2040, 60% en 2050 par rapport à 2010.</p>
<p>Décret n° 2013-695 du 30 juillet 2013 relatif à la réalisation et à l''affichage du diagnostic de performance énergétique dans les bâtiments accueillant des établissements recevant du public de la 1ère à la 4ème catégorie</p>
<p>Ce décret étend l’obligation de réalisation et d’affichage du DPE aux bâtiments ayant une Surface Hors Œuvre Nette (SHON) &gt; 500 m² (échéance au 1er janvier 2015) ou 250 m² (échéance au 1er juillet 2017) ; (sauf piscines et patinoires, pour lesquelles les RT ne s’appliquent pas).</p>
', '', '<p>Diagnostic de Performance énergétique (DPE) :
<a href="http://www.observatoire-dpe.fr/">
http://www.observatoire-dpe.fr/
</a></p>
<p>Plateforme OPERAT, ADEME,
<a href="https://operat.ademe.fr/#/public/accueil">
https://operat.ademe.fr/#/public/accueil
</a></p>
', '<p>Pour les DOM : Ne pas noter ici les usages de l''électricité pour l''ecs, le rafraîchissement et la climatisation (notés à la mesure 2.2.1) mais les autres usages de l''électricité</p>
<p>La notation est commune avec la mesure 2.2.1, en ce qui concerne les effets (basée sur le DPE).</p>
<p>Labellisation unique ville-EPCI :</p>
<ul>
<li>
<p>Des indicateurs distincts peuvent être suivis ou gardés en mémoire par les deux collectivités mais la notation Cit''ergie est basée sur un indicateur commun additionnant les classes énergétiques, nombres et surfaces des bâtiments des deux collectivités.</p>
</li>
<li>
<p>En cas de compétences déléguées, l''intercommunalité organise des réunions avec le gestionnaire pour obtenir les informations et les communiquer à l''échelon communal.</p>
</li>
</ul>
<p>Pour les indicateurs DPE, l’attribution des points est progressive selon les performances de la collectivité, se référer au tableau des indicateurs Cit’ergie.</p>
', '<p>Pas de réduction de potentiel</p>
', 6.0, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2.1', 'cae', '2.2.2.1', 'Mettre en place des actions de sobriété pour limiter les besoins en électricité', '<p>En priorité, des dispositifs de sobriété sont mis en place pour limiter les besoins électriques :</p>
<ul>
<li>
<p>éclairage naturel</p>
</li>
<li>
<p>extinction des équipements lorsqu’ils ne sont pas utilisés</p>
</li>
<li>
<p>limiter au strict minimum l’installation d’équipements consommateurs d’électricité non essentiels dans les bâtiments (écrans muraux, distributeurs…)</p>
</li>
<li>
<p>mutualisation optimale des équipements qui peuvent être partagés (imprimantes, etc.)</p>
</li>
<li>
<p>tc.</p>
</li>
</ul>
<p>Les actions en lien avec la sensibilisation des usagers des bâtiments aux éco-gestes sont traitées dans les mesures du domaine 5 et 6.</p>
', '', '', '', '', '', null, 15.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2.2', 'cae', '2.2.2.2', 'Mettre en place des indicateurs en kWh/m2/an d''énergie primaire par catégories de bâtiments pour les usages électriques et les comparer avec des valeurs de références locales.', '', '', '', '', '', '', null, 5.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2.3', 'cae', '2.2.2.3', 'Mettre en place des actions d’efficacité énergétique sur l’électricité pour le patrimoine spécifique', '<p>Pour le patrimoine spécifique (très gros équipements sportifs ou culturels) :</p>
<ul>
<li>
<p>des études spécifiques ont été menées pour diminuer les consommations de ces équipements et des actions mises en place</p>
</li>
<li>
<p>pour les piscines, un ratio de consommation en kWh/m² est collecté et suivi ; exemple : valeur moyenne française issue de l''enquête Energie et Patrimoine communal 2017 : pour le bassin (3410 kWh/m² en moyenne) / pour l’ensemble du complexe aquatique (730 kWh/m² en moyenne)</p>
</li>
</ul>
<p>Pour les bâtiments équipés d''installations de production de froid : favoriser des installations de production de froid performantes. Une attention est portée au coefficient d''efficacité frigorifique (EER) et pour les PAC réversibles, des labels comme Eurovent ou NF PAC sont recherchés.</p>
', '', '', '', '', '', null, 10.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2.4', 'cae', '2.2.2.4', 'Rénover les bâtiments les plus énergivores', '', '', '', '', '', '', null, 20.0, 'effets');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2.4.1', 'cae', '2.2.2.4.1', 'Ne plus avoir de bâtiments de catégorie F ou G (DPE énergie)', '<p>Ou ayant des consommations &gt;= 701 kWhEP/m².an dans les DOM. l’objectif visé est de ne plus avoir de bâtiment en classe F ou G à court terme.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2.4.2', 'cae', '2.2.2.4.2', 'Posséder au moins 30% de bâtiment en classe A ou B à court terme', '<p>Posséder au moins 30% des bâtiments de classe A ou B (DPE énergie) (ou ayant des consommations =&lt;100 kWhEP/m² dans les DOM).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2.5', 'cae', '2.2.2.5', 'Constater la baisse des consommations moyennes globales des bâtiments publics*', '<ul>
<li>À patrimoine constant (par exemple, la baisse des consommations dûe à la vente du patrimoine énergivore n’est pas prise en compte).</li>
</ul>
', '', '', '', '', '', null, 50.0, 'effets');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2.5.1', 'cae', '2.2.2.5.1', 'Diminuer ses consommations en kWh/hab (kWh/m² pour les EPCI)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.2.5.2', 'cae', '2.2.2.5.2', 'Inscrire cette diminution dans le rythme imposé par le décret tertiaire', '<p>Pour les bâtiments soumis au décret tertiaire, le rythme imposé est de: -40 % en 2030, -50 % en 2040 et -60 % en 2050 par rapport à 2010 (ou par référence aux valeurs absolues fixées réglementairement)</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.1', 'cae', '2.2.1', 'Augmenter l''efficacité énergétique pour la chaleur et le rafraîchissement des bâtiments publics', '<p>La collectivité met en place une démarche de sobriété énergétique, systématise l''efficacité énergétique, pour le chauffage, l''eau chaude et le rafraîchissement de ses bâtiments et l''évalue au moyen d''indicateurs énergétiques pour différentes catégories de bâtiments.</p>
', '<p><strong>Contexte et enjeux</strong></p>
<p>D’après l’enquête « Energie et patrimoine communal », le budget énergie d’une commune représente en moyenne 4% de son budget de fonctionnement. C’est un poste conséquent qu’il est possible de réduire en augmentant l’efficacité énergétique pour tous la chaleur et le rafraichissement des bâtiments publics.</p>
<p><strong>Réglementation</strong></p>
<p>Loi ELAN, article 175 : pour les bâtiments tertiaires réduction de la conso d''énergie finale de 40% en 2030, 50% en 2040, 60% en 2050 par rapport à 2010.</p>
<p>Décret n° 2013-695 du 30 juillet 2013 relatif à la réalisation et à l''affichage du diagnostic de performance énergétique dans les bâtiments accueillant des établissements recevant du public de la 1ère à la 4ème catégorie</p>
<p>Ce décret étend l’obligation de réalisation et d’affichage du DPE aux bâtiments ayant une Surface Hors Œuvre Nette (SHON) &gt; 500 m² (échéance au 1er janvier 2015) ou 250 m² (échéance au 1er juillet 2017) ; (sauf piscines et patinoires, pour lesquelles les RT ne s’appliquent pas).</p>
', '', '<p>Diagnostic de Performance énergétique (DPE) :
<a href="http://www.observatoire-dpe.fr/">
http://www.observatoire-dpe.fr/
</a></p>
<p>Plateforme OPERAT, ADEME,
<a href="https://operat.ademe.fr/#/public/accueil">
https://operat.ademe.fr/#/public/accueil
</a></p>
', '<p>La notation est en grande partie commune avec la mesure 2.2.2.</p>
<p>Ne pas évaluer ici les actions de sensibilisation des usagers des bâtiments aux éco-gestes :</p>
<ul>
<li>
<p>5.1.3 : Former et mobiliser les élus et le personnel</p>
</li>
<li>
<p>6.5.2 : Inciter les citoyens et les relais d''opinions à une consommation responsable, limiter l''emprise de la publicité</p>
</li>
<li>
<p>6.5.3 : Développer des actions d''éducation et de sensibilisation dans les établissements scolaires et centres de petite enfance</p>
</li>
</ul>
<p>Labellisation unique ville-EPCI :</p>
<ul>
<li>
<p>Des indicateurs distincts peuvent être suivis ou gardés en mémoire par les deux collectivités mais la notation Cit''ergie est basée sur un indicateur commun additionnant les classes énergétiques, nombres et surfaces des bâtiments des deux collectivités.</p>
</li>
<li>
<p>En cas de compétences délégués, l''intercommunalité organise des réunions avec le gestionnaire pour obtenir les informations et les communiquer à l''échelon communal.</p>
</li>
</ul>
<p>Pour les indicateurs DPE, l’attribution des points est progressive selon les performances de la collectivité, se référer aux indicateurs.</p>
', '<p>Pas de réduction de potentiel</p>
', 6.0, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.1.1', 'cae', '2.2.1.1', 'Mettre en place des actions de sobriété pour limiter les besoins en chauffage et climatisation', '<p>En priorité, des dispositifs de sobriété sont mis en place (ferme-portes, volets…). Des dispositifs bioclimatiques et tous les paramètres du confort thermique sont étudiés pour limiter le recours à la climatisation.</p>
<p>Les actions en lien avec la sensibilisation des usagers des bâtiments aux éco-gestes sont traitées dans les mesures du domaine 5 et 6 (5.1.3 pour les agents et élus, 6.5.2 pour les associations et 6.5.3. dans les établissements scolaires)</p>
', '', '', '', '', '', null, 15.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.1.2', 'cae', '2.2.1.2', 'Mettre en place des indicateurs en kWh/m2/an d''énergie primaire par catégories de bâtiments pour les usages chaleur/rafraîchissement et les comparer avec des valeurs de références locales', '', '', '', '', '', '', null, 5.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.1.3', 'cae', '2.2.1.3', 'Mettre en place des actions d’efficacité énergétique pour la chaleur et le rafraîchissement du patrimoine spécifique', '<p>Pour le patrimoine spécifique (très gros équipements sportifs ou culturels) :</p>
<ul>
<li>
<p>pour les piscines couvertes, un ratio de consommation en kWh/m2 de bassin est collecté et suivi (ex : valeur moyenne française issue de l''enquête Energie et Patrimoine communal 2012 : 954 kWh/m2 chauffé de bassin)</p>
</li>
<li>
<p>Mener des études spécifiques pour diminuer les consommations de ces équipements et mettre en place des actions.</p>
</li>
</ul>
<p>Pour les bâtiments équipés d''installations de production de froid : favoriser des installations de production de froid performantes. Une attention est portée au coefficient d''efficacité frigorifique (EER) et pour les PAC réversibles, des labels comme Eurovent ou NF PAC sont recherchés.</p>
', '', '', '', '', '', null, 10.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.1.3.1', 'cae', '2.2.1.3.1', 'Mener des études spécifiques pour diminuer les consommations de ces équipements et mettre en place des actions', '', '', '', '', '', '', null, null, null);
