insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.5', 'cae', '2.2.5', 'Limiter les émissions de gaz à effet de serre des bâtiments publics', '<p>La collectivité réduit les émissions de gaz à effet de serre générées par le fonctionnement de ses bâtiments, et intègre également une réflexion sur l’ensemble de leur cycle de vie.</p>
<p>La collectivité évalue son avancement par rapport à ses objectifs de réduction d''émissions de CO2 et de gaz à effet de serre sur les différentes catégories de bâtiments.</p>
', '<p><strong>Contexte et enjeux</strong></p>
<p>En France, le secteur du bâtiment (tertiaire, résidentiel) est le plus gros consommateur d’énergie (42,5 % de l’énergie finale totale consommée), et chaque année, cette consommation équivaut à plus d’une tonne équivalent pétrole par français. Les bâtiments sont responsables de 19 % des émissions de GES, dont les 2/3 par les logements.</p>
<p><strong>Réglementation</strong></p>
<p>La Loi de transition énergétique (17 aout 2015) a fixé comme objectif de de réduire de 40 % les émissions de gaz à effet de serre en 2030 par rapport à 1990.</p>
', '', '', '<p>Lien vers les mesures :</p>
<ul>
<li>
<p>2.1.3 : Être exemplaires sur les bâtiments publics neufs et rénovés</p>
</li>
<li>
<p>5.2.2 : Être exemplaire en matière d’éco-responsabilité de la commande publique</p>
</li>
</ul>
', '<p>Pas de réduction de potentiel</p>
', 6.0, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.5.1', 'cae', '2.2.5.1', 'Effectuer des bilans GES plus complets que la réglementation ne l’impose', '', '', '', '', '', '', null, 20.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.5.1.1', 'cae', '2.2.5.1.1', 'Fixer des cibles en termes d''émissions de GES pour les bâtiments non soumis aux règlementations thermiques ou au DPE (comme les piscines ou les patinoires)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.5.1.2', 'cae', '2.2.5.1.2', 'Aller plus loin que le DPE ou le bilan GES « patrimoine et compétences » réglementaires pour effectuer le bilan GES de ses bâtiments (bilan GES sur émissions directes et indirectes en distinguant par exemple les usages de l''électricité)', '', '', '', '', '', '', null, null, null);
