insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.5.2.2', 'cae', '2.2.5.2.2', 'Porter une attention particulière au pouvoir de réchauffement global (PRG) des fluides frigorigènes utilisés, à la qualité des opérations de maintenance (installateur qualifié, suivi des quantités de fluides rechargées, récupération des fluides, pertes minimes...) et au traitement en fin de vie de ces équipements.', '<p>Seulement pour les bâtiments et équipements disposant d''installations de production de froid (réfrigérateur, congélateur, climatiseur...)</p>
', '', '', '', '', '', null, null, null);
