insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.4.1.2', 'cae', '2.2.4.1.2', 'Prendre connaissance des différentes offres existantes des fournisseurs d''électricité (offres EnR, garanties d''origine, critères de qualité environnementale...) et s''organiser pour consulter le marché (connaissance fine de ses consommations par point de livraison)', '', '', '', '', '', '', null, null, null);
