insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.4.2.2', 'cae', '2.2.4.2.2', 'Vendre prioritairement à des acteurs (acheteurs, fournisseurs) proposant une offre renouvelable de haute qualité', '<p>Seulement si la production d’électricité verte est commercialisée en dehors de l''obligation d''achat. Offre de haute qualité: 100% EnR, qualité environnementale, transparence, critère d''additionnalité, fond de développement des EnR...</p>
', '', '', '', '', '', null, null, null);
