insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.2.5.1', 'cae', '2.3.2.5.1', 'Diminuer les consommations/m2 par rapport aux années précédentes et communiquer officiellement (ex : rapport de développement durable, bilan énergie/GES...)', '', '', '', '', '', '', null, null, null);
