insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.1.3.1', 'cae', '2.3.1.3.1', 'Mettre en œuvre de mesures d''amélioration des performances issues du diagnostic', '<p>Exemples de mesures: Optimisation tarifaire, maintenance préventive, modernisation des équipements, modification/optimisation du service rendu – extinction ou variateur de tension la nuit, choix judicieux des points lumineux, généralisation de l’éclairage à détection de présence ou encore la modération de l’éclairage des bâtiments, statues et façades...</p>
', '', '', '', '', '', null, null, null);
