insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.1.3.3', 'cae', '2.3.1.3.3', 'Inscrire des mesures de limitation des consommations énergétiques dans un document formalisé, un plan pluriannuel d’optimisation (Schéma directeur d''aménagement lumière / Plan lumière, Plan d’aménagement Lumière ou autre document...)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.1.3.4', 'cae', '2.3.1.3.4', 'Porter une attention à la concertation avec la population et à la valorisation de la démarche', '<p>Réunion publique, observation des étoiles, information sur le découplage sécurité des riverains/éclairage, bénéfices sur la biodiversité, labellisation « villes et villages étoilés »...</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.1.3.5', 'cae', '2.3.1.3.5', 'Identifier les enseignes lumineuses illégales et prendre des mesures d’accompagnement pour y remédier (prise en compte de l’énergie dans le règlement local de publicité quand il existe)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.1.3.6', 'cae', '2.3.1.3.6', 'Intégrer une opération de portage collectif (EPCI, syndicat d’énergie, Agence locale de l’énergie) sur l’optimisation de l’éclairage public', '', '', '', '', '', '', null, null, null);
