insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.1.3.8', 'cae', '2.3.1.3.8', 'Respecter les prescriptions techniques de l’arrêté du 27 décembre 2018 relatif à la prévention, à la réduction et à la limitation des nuisances lumineuses', '<p>Prescriptions techniques: températures de couleur strictement inférieures à 3000K (idéal = 2700K) ; ratio ULR=0 (rapport du flux sortant des luminaires qui est émis dans l’hémisphère supérieur au flux total sortant des luminaires) ; densité surfacique de flux lumineux installé (lumen/m²) limitée à 35 lm/m2 en agglomération et 25lm/m2 hors agglomération.</p>
', '', '', '', '', '', null, null, null);
