', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.1.1.2.2', 'cae', '6.1.1.2.2', 'Echanger et coopérer avec les institutions nationales et régionales impliquées dans les PCAET, SRCAE/SRADDET et PPA (DREAL, DDT, Ministère de l''écologie...)', '<p>Notamment sur la thématique de la vulnérabilité au changement climatique ou la qualité de l''air.</p>
', '', '', '', '', '', null, null, null);
