insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.1.3.4.3', 'cae', '6.1.3.4.3', 'Mettre en place des actions (évènements) nombreuses et variées (lieux, formes, horaires) sur les thèmes climat-air-énergie.', '<p>La fréquence de diffusion des informations est adaptée à la taille de la collectivité : 2 actions /an (&lt; 3 000 hab), 5 actions /an (&gt;3 000 hab), 10 actions /an (&gt;50 000 hab).</p>
', '', '', '', '', '', null, null, null);
