insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.1.2.1.2', 'cae', '6.1.2.1.2', 'Etre à l’initiative ou participer à des rencontres ponctuelles bilatérales commune/intercommunalité en lien avec le climat, l’air et l’énergie', '<p>par exemple : conférences, groupes de travail thématique, rencontres à l’occasion de l’élaboration des documents de planification et d’urbanisme. Pour les EPCI : les rencontres sont fréquemment délocalisées sur le territoire pour les territoires étendus pour faciliter la participation d’un maximum de communes membres.</p>
