insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.1.2.2.1', 'cae', '6.1.2.2.1', 'Sur les thématiques en lien avec le climat, l’air et l’énergie, les rôles et compétences de chacun (intercommunalité/commune) sont clairs', '<p>Le cas échéant des précisions sont apportées et formalisées (délibération précisant les compétences, convention EPCI-commune membre)</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.1.2.2.2', 'cae', '6.1.2.2.2', 'Les interlocuteurs climat-air-énergie principaux dans chaque collectivité sont connus et ont été rencontrés', '<p>Par exemple : le chef de projet « énergie » de l’intercommunalité a un interlocuteur privilégié au sein de chaque commune membre, les homologues sur le sujet de l’énergie entre communes membres se connaissent</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.1.2.3', 'cae', '6.1.2.3', 'Initier et/ou participer à des actions mutualisées sur le territoire', '<p>Exemples de projets collectifs concernés (initiation ou participation a minima 2 actions collectives durant les 4 dernières années) :</p>
<p>− Conseil en énergie partagé</p>
<p>− Opération collective d''élaboration de stratégie immobilière (sdi)</p>
<p>− Achat groupé d''énergie renouvelable</p>
<p>− Opération collective d''élaboration de plan de déplacement administration,</p>
<p>− Valorisation centralisée de CEE</p>
<p>− Accompagnement des communes au renforcement de l''achat durable (outils, grilles de critères communs)</p>
<p>− Formation collective des agents communaux sur des thématiques techniques en lien avec le climat l’air et l’énergie (ex : formation des secrétaires de mairie)</p>
<p>− Etc.</p>
', '', '', '', '', '', null, 40.0, 'mise en œuvre');
