insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.3.1.3', 'cae', '6.2.3.1.3', 'Disposer d’un service d''information neutre pour les questions concernant le climat, l''air et l''énergie dans le bâtiment pour les particuliers', '<p>Propre à la collectivité ou via une participation active à un service d''information local/départemental/régional (pour les collectivités inférieures à 10 000 habitants)</p>
