insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.3.2', 'cae', '6.2.3.2', 'Adresser des conseils pour la rénovation BBC notamment, et la construction, à des cibles variées', '<p>Exemple pour une commune non compétente : elle repère les différentes cibles et fait une communication différenciée, elle facilite le repérage des bénéficiaires en relayant les opérations portées par l’espace conseil FAIRE...</p>
<p>Cibles variées: propriétaires (occupant ou bailleur), locataires, logement individuel ou collectif (dont copropriété), résidence principale ou secondaire (selon enjeu sur le territoire), personnes nouvellement établies</p>
', '', '', '', '', '', null, 20.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.3.3', 'cae', '6.2.3.3', 'Accompagner le particulier jusqu’aux travaux de rénovation énergétique', '<p>Les ECF sont abordés également dans les mesures 1.2.4 (volet planification) et 6.2.1 (volet mobilisation des professionnels du bâtiment).</p>
<p>Exemple pour une commune non compétente : elle participe à la gouvernance du dispositif d''accompagnement (si autorisé par la structure compétente), elle en connait les modalités précisemment et le relaie auprès de sa population...</p>
', '', '', '', '', '', null, 20.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.3.3.1', 'cae', '6.2.3.3.1', 'S''appuyer a minima sur les missions de son espace conseil FAIRE pour guider le particulier dans son parcours de rénovation de son logement.', '<p>L''accompagnement le plus complet possible est recherché : aide à la réalisation d''un audit énergétique, fléchage artisan RGE, analyse des devis, aides au montage des dossiers de subvention)</p>
', '', '', '', '', '', null, null, null);
