insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.4.2.1', 'cae', '6.2.4.2.1', 'Accompagner concrètement les entreprises du secteur tertiaire (TPE/PME et commerçants notamment) et les artisans pour améliorer la performance climat-air-énergie de leurs locaux en partenariat avec les acteurs spécialisés (chambres consulaires...)', '<p>Soutien méthodologique ou financier, par exemple : appels à projets pour inciter aux initiatives innovantes ou exemplaires, fonds de soutien aux projets lauréats, opération collective de soutien à l’élaboration de plan d’actions de réduction de gaz à effet de serre, désignation/mise à disposition d’un animateur référent tertiaire, signature de charte d’engagement climat-air-énergie (objectifs qualitatifs et/ou quantitatif sur les consommations d’énergies, les émissions de gaz à effet de serre, le confort d''été et de polluants atmosphériques)</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.4.2.2', 'cae', '6.2.4.2.2', 'Soutenir spécifiquement les entreprises tertiaires > 1000 m² soumises aux obligations du "Décret Tertiaire"', '<p>Decret Tertiaire: obligation de mise en œuvre d’actions de réduction de la consommation d’énergie finale dans les bâtiments existants à usage tertiaire afin de parvenir à une réduction de la consommation d’énergie finale pour l’ensemble des bâtiments soumis à l’obligation d’au moins 40 % en 2030, 50 % en 2040 et 60 % en 2050 par rapport à 2010. Exemple de soutien: lettre d''information, club d''échanges, visites de site, etc.</p>
', '', '', '', '', '', null, null, null);
