insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.1.3', 'cae', '6.2.1.3', 'Les bailleurs sociaux sont engagés aux côtés de la collectivité dans des actions particulièrement ambitieuses', '<p>Par exemple : création d''un référentiel de performance énergie-climat pour les opérations de logements sociaux, signature d''accords/de convention/d''une charte d''engagement entre la collectivité et les bailleurs, par exemple dans le cadre d''un CLE ou de programmes ANRU, éco-conditionnement des garanties d''empunts accordés aux bailleurs sociaux, soutien à la réalisation de diagnostic de performance énergétique dans les logements ou aux investissements écologiques (soutien technique, conseil, groupement d''achats, montage commun de dossier de certificats d''économie d''énergie...).</p>
', '', '', '', '', '', null, 20.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.1.4', 'cae', '6.2.1.4', 'Disposer d’une stratégie de mobilisation des professionnels du bâtiment et de l’immobilier formalisée', '<p>La collectivité dispose d’une stratégie de mobilisation des professionnels formalisée, articulée avec les actions des autres échelons de gouvernance (département, région, Etat), et dotée de moyens dédiés (par exemple via une plateforme territoriale de la rénovation, un centre de ressources BEEP qu’elle soutient, etc.)</p>
', '', '', '', '', '', null, 20.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.3', 'cae', '6.3', 'Activités économiques', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.3.3', 'cae', '6.3.3', 'Contribuer à l''innovation et l''excellence sur la thématique climat-air-énergie', '<p>La collectivité contribue à l’innovation et l’excellence sur la thématique climat-air-énergie. Elle peut collaborer avec les centres de formation et le milieu de la recherche ou soutenir un projet phare exemplaire et ambitieux privé, dans ces mêmes domaines, en apportant son expertise et/ou ses conseils pour pousser le projet plus loin dans l’excellence. Elle peut en outre favoriser l’émergence de projets en fédérant les acteurs (organisation de rencontres entre collectivité, entreprises, associations et chercheurs) ou en facilitant le dépôt de projets (recherche des projets, soutien méthodologique).</p>
<p>En particulier, la collectivité est cohérente dans sa prise de position à l’égard des grands projets impactant en matière d’émission de gaz à effet de serre, de qualité de l’air ou la consommation d’énergie ou l’utilisation des terres agricoles sous l’influence de son territoire.</p>
', '<p><strong>Contexte et enjeux</strong></p>
<p>Le soutien à la recherche et l’innovation constitue un des axes majeurs de la transition énergétique pour accompagner les filières correspondantes vers la maturité et la compétitivité, au niveau national. Grâce au partenariat public-privé et à leur ancrage local, les collectivités peuvent participer, dans la mesure de leurs moyens, à l’innovation et l’excellence dans le domaine climat-air-énergie.</p>
<p><strong>Réglementation</strong></p>
', '<p><strong>Communauté urbaine de Dunkerque</strong></p>
<p>Développement de la filière Hydrogène</p>
<p>Dans le cadre du programme des investissements d’avenir piloté par l’ADEME, le projet de démonstration de « Gestion des Réseaux par l’injection d’Hydrogène pour Décarboner les énergies » a pour objectif d’évaluer en 4 ans la pertinence technico-économique d’une nouvelle filière énergétique composée de mélange d’hydrogène et de gaz naturel sur différents marchés.</p>
<p>Soutenues par des actions de recherche industrielle, deux démonstrations de production, distribution et utilisation locale d’hydrogène, à terme d’origine renouvelable, seront réalisées sur le territoire de la Communauté Urbaine de Dunkerque :</p>
<p>‐ volet « Habitat/logement » : injection d’hydrogène dans le réseau gaz naturel (entre 6 % et 20 %) pour satisfaire les besoins de chaleur de 200 nouveaux logements</p>
<p>‐ volet « Transport » : carburant Hythane (80 % de gaz naturel et 20 % d’hydrogène). Utilisé dans une cinquantaine de bus existants fonctionnant au GNV, il permet, moyennant des adaptations rapides et à moindre coût, d’introduire de l’hydrogène pour diminuer de 7 % les émissions de CO2 et de 10 % celles des NOx.</p>
<p>→ Source : dossier de candidature Cit’ergie GOLD, 2013 + recueil de BP Cit’ergie</p>
<p><strong>CA Chalon-sur-Saône</strong></p>
<p>Soutenir des projets locaux d’économie circulaire</p>
<p>Le Grand Chalon a soutenu le développement d’une filière locale de fabrication d’un isolant composé de ouate de cellulose issu de chutes de carton, mis en place par une entreprise d’insertion par l’activité économique. La seconde étape à mettre en œuvre est la récupération des cartons issus de la collecte réalisée par le Grand Chalon auprès de ses commerçants pour les intégrer dans le process de fabrication.</p>
<p>L’isolant a obtenu dernièrement les certifications ACERMI et CQMB, ainsi le Grand Chalon peut cibler cet isolant dans les consultations pour les travaux d’isolation des bâtiments publics.</p>
<p>→ Source : dossier de candidature Cap Cit’ergie, 2016 + recueil de BP Cit’ergie</p>
', '', '<p>Les relations nouées avec les centres de formation ou de recherche situés en dehors du territoire sont pris en compte.</p>
<p>Lien avec l’action 6.3.1 « Favoriser les activités économiques durables et valoriser les ressources locales »</p>
', '<p>Pas de réduction de potentiel</p>
', 6.0, null, null);
