insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.3.3.3.1', 'cae', '6.3.3.3.1', 'Mener des études et recherches avec les organismes de formation (stagiaires, apprentis), les établissements d''enseignements supérieurs ou de recherche', '<p>Par exemple sur: éco-matériaux, efficacité énergétique dans les bâtiments ou les transports, agriculture durable (notamment les liens air et agriculture), surveillance des pollens, qualité de l''air intérieur, gestion des risques dans le cadre de l''adaptation au changement climatique, études des liens entre les conditions locales de circulation et les concentrations de polluants atmosphériques ...</p>
