insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.3.3.4.1', 'cae', '6.3.3.4.1', 'Les projets soutenus vont au-delà de la réglementation, intègrent des performances environnementales et climatiques chiffrées, dans une approche transversale et sont menés dans une approche participative.', '', '', '', '', '', '', null, null, null);
