insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.3.1.5.1', 'cae', '6.3.1.5.1', 'Renforcer le rôle de relais des commerçants et artisans auprès de leurs clients, pour modifier les habitudes de consommation', '<p>Orienter vers des produits à plus faible impact environnemental, de fabrication locale, affichage, mise à disposition de document de sensibilisation pour les consommateurs, etc...</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.3.1.5.2', 'cae', '6.3.1.5.2', 'Soutenir l’artisanat et les commerces de proximité', '<p>par des exonérations de charge locale (foncière par exemple), par des mesures particulières dans le PLU, par une mise en valeur des produits/savoirs faires locaux ou à faible impact environnemental (offres touristiques, partenariat dans le cadre des comités d’entreprises, création d’un label local etc.), par des opérations FISAC...</p>
', '', '', '', '', '', null, null, null);
