insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.2.2.2', 'cae', '6.4.2.2.2', 'Dresser une liste des produits alimentaires dont la production est cause de déforestation (par exemple : soja, huile de palme, bœuf et se coproduits, cacao, hévéa) et contrôler leur provenance et les méthodes de production lorsqu’ils sont utilisés pour la restauration publique', '<p>S’ils ne proviennent pas de cultures durables, ils sont limités voire interdits.</p>
