insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.2.3.2', 'cae', '6.4.2.3.2', 'Réaliser un diagnostic de la forêt', '<p>Inlucre dans le diagostic les surfaces par typologie des forêts, les stocks de carbone et la séquestration de carbone par la forêt, le taux des prélèvements, la répartition de la récolte entre les différents usages du bois, de ses usages et de ses acteurs sur le territoire.</p>
', '', '', '', '', '', null, null, null);
