insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.2.4.1', 'cae', '6.4.2.4.1', 'La collectivité met en œuvre avec les acteurs du territoire un plan de préservation et de valorisation de la forêt', '<p>Doit porter sur:</p>
<p>o la gestion et la préservation des zones forestières ;</p>
<p>o le développement de la diversité biologique, la protection des ressources hydriques, des sols et paysages ;</p>
<p>o la préservation de la vitalité et de la capacité de régénération dans un contexte de changement climatique ;</p>
<p>o le développement des documents de gestion;</p>
<p>o l’évaluation des ressources en bois pour le développement des filières ;</p>
<p>o la régulation des usages de la forêt (ex : la canalisation du public sur les chemins, la prescription des loisirs motorisés,..) ;</p>
<p>o l’évaluation des actions permettant d’optimiser la contribution de la forêt et le bois à l’atténuation du changement climatique tout en favorisant l’adaptation au changement climatique.</p>
<p>o Des indicateurs concernant par exemple la vulnérabilité, la séquestration carbone, le taux de prélèvements et la répartition de la récolte entre les différents usages du bois, la contribution de la forêt.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.2.4.2', 'cae', '6.4.2.4.2', 'Prendre en compte les zones boisées dans les documents d’urbanisme (zonage approprié, espaces boisés classés, éléments remarquables à protéger et repérés dans les documents graphiques)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.2.4.3', 'cae', '6.4.2.4.3', 'Articuler sa stratégie avec les politiques locales de développement (Chartes de Pays, etc.) et les autres dispositifs de gestion forestière (schémas régionaux de gestion forestière, plan de développement de massif) en co-construction avec les acteurs locaux concernés (ex : les Chartes forestières)', '', '', '', '', '', '', null, null, null);
