insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.4', 'cae', '6.4.1.4', 'Reterritorialiser l’alimentation de la collectivité et contribuer à sa résilience alimentaire', '', '', '', '', '', '', null, 15.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.4.1', 'cae', '6.4.1.4.1', 'Accompagner la diversification des productions agricoles locales pour répondre aux besoins du territoire (légumes secs, fruits et légumes frais de saison, etc.)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.4.2', 'cae', '6.4.1.4.2', 'Intégrer les questions d’approvisionnement alimentaire (durable) dans les réflexions urbanistiques : types de commerces, marchés de détails et marché de gros (MIN), accès aux modes doux…', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.4.3', 'cae', '6.4.1.4.3', 'Favoriser l’accès à la terre et les regroupements de producteurs locaux pour optimiser la logistique du dernier kilomètre et l’installation de boutiques paysans', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.4.4', 'cae', '6.4.1.4.4', 'Valoriser les produits locaux / filières locales de qualité (création de marché de producteurs locaux, guide, cartographie…)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.4.5', 'cae', '6.4.1.4.5', 'En milieu urbain, accompagner l’installation de fermes urbaines durables ; de jardins partagés ou pédagogiques, des ruches (dans le respect de la biodiversité locale de pollinisateurs), les plantations comestibles en milieu urbain, encourager la multiplication des potagers privés (sensibiliser aux pratiques agro-écologiques, enjeux environnementaux et sanitaires)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.4.6', 'cae', '6.4.1.4.6', 'Favoriser / soutenir les initiatives d’accès à tous à une alimentation saine et durable. Faire se rencontrer les acteurs de l’alimentation, de la santé et du social', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.4.7', 'cae', '6.4.1.4.7', 'Mettre en place ou soutenir les actions de sensibilisation du grand public à l’alimentation durable', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.4.8', 'cae', '6.4.1.4.8', 'Préserver les terres agricoles via les documents d’urbanisme', '<p>Evalué dans la mesure 1.3.1.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5', 'cae', '6.4.1.5', 'Modifier les habitudes alimentaires, notamment dans la restauration collective, en respectant les obligations de la loi EGalim et en s’engageant plus loin', '', '', '', '', '', '', null, 15.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.1', 'cae', '6.4.1.5.1', 'Revoir les approvisionnements en restauration collective publique', '<p>A minima 50% de produits durables et de qualité, dont au moins 20% issus de l’agriculture biologique (critères Egalim) ; privilégier les produits locaux ou ancrés dans les territoires (PAT); servir uniquement des fruits et légumes frais de saison, favoriser les produits issus du commerce équitable.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.2', 'cae', '6.4.1.5.2', 'Informer les convives sur l’origine, la saisonnalité et la qualité des produits servis (Egalim et autres critères de qualité / durabilité); mettre en place un affichage environnemental et nutritionnel des menus', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.3', 'cae', '6.4.1.5.3', 'Diversifier les apports alimentaires, en cohérence avec les nouvelles recommandations de Santé Publique France', '<p>Notamment : mettre en place un plan de diversification protéique, introduisant notamment les légumineuses (obligatoire à partir de 200 couverts/j, Egalim) ; proposer au moins un menu végétarien hebdomadaire (obligatoire en restauration scolaire, Egalim) et/ou une alternative végétarienne (choix multiples).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.4', 'cae', '6.4.1.5.4', 'Lutter contre le gaspillage alimentaire (ajuster les quantités aux besoins, sensibiliser les convives, organiser des pesées annuelles sur une semaine, etc.)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.5', 'cae', '6.4.1.5.5', 'Lors des achats de matériels/rénovations importantes, réfléchir sur la globalité du repas (besoin en légumerie, cuisson basse température, contenants de différentes tailles pour les petites/grandes faims, salade bar, …)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.6', 'cae', '6.4.1.5.6', 'Former les cuisinier·es et le personnel de cantine à l''introduction de produits bio locaux et à la diversification des protéines, et plus globalement aux enjeux environnementaux, et leur fournir un corpus de recettes adaptées à la restauration collective. Les former également aux démarches de réduction du gaspillage alimentaire.', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.7', 'cae', '6.4.1.5.7', 'Former les acheteur·ses et les gestionnaires impliqué·es dans la restauration collective aux questions environnementales, sociales, sanitaires de l’alimentation, aux achats bios/locaux et aux démarches de réduction du gaspillage alimentaire.', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.8', 'cae', '6.4.1.5.8', 'Mener des actions spécifiques en restauration scolaire', '<p>En lien avec la mesure 6.5.4</p>
<p>Organiser des ateliers sur la provenance des aliments consommés par les enfants (dont les protéines animales), sur l''impact environnemental de leur assiette, pour les produits locaux ou d’importation faisant partie de nos habitudes alimentaires</p>
<p>Organiser des visites de fermes d’où proviennent les aliments cuisinés ou des interventions des producteurs</p>
<p>Organiser avec les enfants des pesées d’assiette, et le tri sélectif des déchets, pour agir sur le gaspillage alimentaire</p>
<p>Co-construire les menus avec eux·elles (et le·a diététicien·ne de la ville)</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.6', 'cae', '6.4.1.6', 'Participer concrètement à des installations et au maintien des pratiques agricoles durables', '', '', '', '', '', '', null, 15.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.6.1', 'cae', '6.4.1.6.1', 'Créer une régie agricole et/ou utiliser de baux environnementaux', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.6.2', 'cae', '6.4.1.6.2', 'Acquérir, mettre en réserve ou mettre à disposition du foncier en partenariats avec des associations (exemple Foncière Terre de Liens)', '<p>Mettre en réserve via une convention de portage foncier EPF ou SAFER ou un stockage en direct par la collectivité</p>
<p>Mettre à disposition du foncier (terres ou bâtiment, fermes communales ou intercommunales, fermes relais, régies communales)</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.6.3', 'cae', '6.4.1.6.3', 'Installer des porteurs de projets exemplaires et créer des espaces-test', '<p>Exemple d''espaces test: mise à disposition de terres, portage ou financement de bâtiments, aide à démarrage et au fonctionnement de la structure d''accueil, portage politique et technique du projet...</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.6.4', 'cae', '6.4.1.6.4', 'dans les DOM, les mesures spécifiques DOM plan Ecophyto sont déclinées/soutenues localement', '<p>Par exemple : plateforme d’expérimentation « systèmes de culture innovants » adaptées au climat tropical, recherche sur les méthodes de lutte biologique…</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.7', 'cae', '6.4.1.7', 'Adopter une vision globale grâce à la formalisation d’un Projet Alimentaire Territorial (PAT)', '<p>Formaliser son PAT (par exemple via la reconnaissance au niveau national par le Ministère de l’agriculture et de l’alimentation) avec des critères écologiques et sociaux, compatible avec le PCAET, prenant en compte la résilience alimentaire et appuyant la structuration de filières paysannes, biologiques et locales, rémunératrices pour les agriculteurs.</p>
', '', '', '', '', '', null, 5.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.8', 'cae', '6.4.1.8', 'Mesurer et atteindre des objectifs ambitieux en matière d’agriculture et d’alimentation durable', '', '', '', '', '', '', null, 15.0, 'effets');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.8.1', 'cae', '6.4.1.8.1', 'Mettre en place et suivre des indicateurs en matière d''intrants (fréquence, quantité, produits...) selon les cultures, soutien et suivi technique des rendements/coûts/impacts environnementaux d’espaces test agricoles…', '<p>En lien avec les chambres d''agriculture, les coopératives agricoles, les ONVAR (CIVAM, GAB, CUMA, etc.) et les agriculteurs</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.8.2', 'cae', '6.4.1.8.2', 'En restauration collective, sur le gaspillage alimentaire, atteindre l’objectif de 50% de réduction par rapport à la référence nationale du secteur', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.8.3', 'cae', '6.4.1.8.3', 'S’engager à au moins 75% de l’alimentation en restauration collective qui soit de qualité ou durable, dont au moins 40% issus de l’agriculture biologique et 50% issue de produits locaux ; poursuivre la diversification alimentaire.', '<p>Dans les DOM, suivi uniquement de l’évolution.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.8.4', 'cae', '6.4.1.8.4', 'Mettre en place un programme pour l’accès à tous à une alimentation saine et durable', '<p>incluant : sensibilisation et accompagnement de la population, soutien à des structures ESS type épiceries solidaires, AMAP, etc.), programme de type chèque alimentaire ou Sécurité sociale alimentaire locale</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.8.5', 'cae', '6.4.1.8.5', 'Avoir un pourcentage élevé de Surface Agricole Utile en agricultue biologique.', '<p>En métropole, objectif 20%, valorisé progressivement à partir de 6% (dans les DOM, suivi uniquement de l’évolution).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.5', 'cae', '6.5', 'Société civile', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.5.3', 'cae', '6.5.3', 'Développer des actions d''éducation et de sensibilisation dans les établissements scolaires et centres de petite enfance', '<p>La collectivité développe des actions d’éducation et de sensibilisation à la problématique climat-air-énergie dans les établissements scolaires, crèches et garderies. Ces actions sont variées (forme et contenu), et impliquent les élèves, le corps enseignant et le personnel. Autant que possible, elles s’intègrent dans un projet pédagogique global et sont planifiées sur l’année voire sur l’ensemble du cycle. Certaines actions visent directement la réduction des consommations d’énergie, d’eau et les émissions de polluants liées à l’utilisation des locaux et aux déplacements scolaires.</p>
', '<p><strong>Contexte et enjeux</strong></p>
<p>La transition énergétique et écologique nécessite une transformation des modes de vie et donc un accompagnement au changement de comportement. « L''éducation pour le développement durable (EDD) vise à doter les individus des comportements, compétences et connaissances qui leur permettront de prendre des décisions éclairées pour eux-mêmes et les autres, aujourd''hui et à l''avenir, et de traduire ces décisions en actes » (Site de l’UNESCO, Education pour le développement durable). La sensibilisation des enfants (cycle primaire principalement), par leur capacité d’apprentissage et leur faculté à transmettre les messages (à leurs parents notamment), est à soigner particulièrement.</p>
<p>Par ailleurs, permettre à toutes les personnes présentes dans l''établissement scolaire de s''impliquer autour d''un projet de développement durable, est très motivant.</p>
<p>L’éco-mobilité scolaire est un succès sur tous les plans : elle engendre des économies financières (baisse du budget transport), des gains environnementaux (réduction des émissions de gaz à effet de serre et des pollutions atmosphériques, sonore et visuelle liées à la voiture), et renforce le lien social et la sécurité aux abords des établissements scolaires en diminuant le trafic. Elle permet en outre aux enfants d’effectuer une activité physique et ainsi de limiter les risques d’obésité infantile. Elle représente également une première approche de l’écocitoyenneté pour les enfants. Le bus pédestre ou cycliste constitue d’ailleurs une excellente opportunité pour les écoles ou associations de sensibilisation des enfants à l’environnement, la santé, la nutrition, etc. De plus, les enfants en sont friands ! Enfin, elle offre aux enfants, aux parents et au maire la possibilité d’aborder la commune sous un angle différent.</p>
<p><strong>Réglementation</strong></p>
<p>Décret du 28 juin 2016 relatif au plan climat air énergie territorial :</p>
<ul>
<li>
<p>Art. R. 229-51 – III : « Le programme d’actions porte sur les secteurs d’activité définis par l’arrêté pris en application de l’article R. 229-52. Il définit des actions à mettre en œuvre par les collectivités territoriales concernées et l’ensemble des acteurs socio-économiques, y compris les actions de communication, de sensibilisation et d’animation en direction des différents publics et acteurs concernés. Il identifie des projets fédérateurs, en particulier ceux qui pourraient l’inscrire dans une démarche de territoire à énergie positive pour la croissance verte, tel que défini à l’article L. 100-2 du code de l’énergie. Il précise les moyens à mettre en œuvre, les publics concernés, les partenariats souhaités et les résultats attendus pour les principales actions envisagées. »</p>
</li>
<li>
<p>Art. R.229-51 – IV : « Le dispositif de suivi et d''évaluation porte sur la réalisation des actions et le pilotage adopté. Il décrit les indicateurs à suivre au regard des objectifs fixés et des actions à conduire et les modalités suivant lesquelles ces indicateurs s''articulent avec ceux du schéma régional prévu à l’article L.222-1 ainsi qu’aux articles L. 4433-7 et L. 4251-1 du code général des collectivités territoriales. Après trois mois d’application, la mise en œuvre du plan climat-air-énergie fait l’objet d’un rapport mis à la disposition du public ».</p>
</li>
<li>
<p>Art. R. 229-53 « la collectivité ou l’établissement public qui engage l’élaboration du plan climat air énergie territorial en définit les modalités d’élaboration et de concertation ».</p>
</li>
</ul>
<p>Décret n°2015-1000 du 17 août 201 5 relatif aux modalités de surveillance de la qualité de l’air intérieur dans certains établissements recevant du public : obligation d''évaluer les moyens d''aération des bâtiments et de mener une campagne de mesures des polluants, sauf pour les établissements ayant mis en place un plan d''actions pour préserver la qualité de l''air intérieur. Obligation en vigueur à partir du 1er janvier 2018 pour les établissements d''accueil collectifs d''enfants de moins de six ans et les écoles maternelles et pour les écoles élémentaires ; 1er janvier 2020 pour les centres de loisirs et les établissements d''enseignement ou de formation professionnelle du second degré (collèges, lycées, etc.) ; 1er janvier 2023 pour les autres établissements.</p>
<p>Compétences des collectivités :</p>
<p>La commune a la charge des écoles publiques établies sur son territoire. Elle est propriétaire des locaux et en assure la construction, la reconstruction, l''extension et les grosses réparations. Elle gère les crédits d''équipement, de fonctionnement et d''entretien des écoles. L''organisation de la restauration relève de la commune. Elle peut organiser des activités éducatives, sportives et culturelles complémentaires au sein de l''école. La commune gère les personnels non enseignants, en particulier les agents territoriaux spécialisés des écoles maternelles (article L212-1 et suivants du code de l’enseignement).</p>
<p>La commune ou les groupements de communes bénéficient d’une compétence facultative en matière d’accueil des jeunes enfants. Chaque commune peut adopter un schéma pluriannuel de développement des services d’accueil des enfants de moins de 6 ans (article L. 214-2   du code de l’action sociale et des familles).</p>
', '<p><strong>CA Chalon-sur-Saône</strong></p>
<p>Éduquer et sensibiliser au développement durable</p>
<p>Les actions de sensibilisation liées aux économies d’énergie mises en œuvre sur le territoire sont réalisées de la façon suivante pour le Jeune public :</p>
<ul>
<li>
<p>Les établissements scolaires reçoivent un programme d’éducation à l’environnement et au développement durable, le service a reçu 340 demandes d’interventions et a réalisé 322 séances sur l’année 2016-2017. Plus de 4 300 élèves ont été sensibilisés, dont 427 sur le thème de l’énergie avec plus de 50 séances.</p>
</li>
<li>
<p>Les jeunes ont été sensibilisés aux éco-gestes au sein du Pôle jeunesse (52 participants au RAID Bourgogne Nature).</p>
</li>
</ul>
<p>→ Source : dossier de candidature CAP Cit’ergie, 2016</p>
<p><strong>Baie-Mahault</strong></p>
<p>Sensibilisation des scolaires</p>
<p>La commune réalise depuis 2012 des actions de sensibilisation auprès des élèves d’écoles élémentaires grâce à un partenariat établi avec le conseiller pédagogique en sciences de l’Éducation Nationale qui permet l’intégration des animations dans le programme scolaire des CP-CM2.</p>
<p>Une animatrice dédiée à ces questions a été recrutée. Les animations touchent un nombre important d’élèves (entre 1 000 et 3 000 par an), plusieurs niveaux de classe, et sont institutionnalisées d’une année à l’autre. Les thèmes sont variés et approfondis comme l’atteste la réalisation par la Ville de l’exposition sur les incidences du changement climatique réalisée par les classes élémentaires. Les animations sont actualisées chaque année pour varier les thèmes.</p>
<p>→ Source : dossier de candidature CAP Cit’ergie, 2015</p>
<p><strong>Vire</strong></p>
<p>Actions avec les scolaires</p>
<ul>
<li>
<p>Carapatte</p>
</li>
<li>
<p>Exposition de 6 semaines organisée avec le SDEC + le Parcours de l’énergie + la sensibilisation de la cellule génie climatique (relevé de compteurs)</p>
</li>
<li>
<p>Compostage des déchets à la cantine par les élèves</p>
</li>
<li>
<p>Suppression des serviettes en papier à la cantine scolaire et distribution de serviettes en tissu.</p>
</li>
</ul>
<p>→ Source : dossier de candidature Cit’ergie, 2013</p>
', '<p>ADEME, Catalogue des outils pédagogiques, mars 2021 :</p>
<a href="https://www.ademe.fr/sites/default/files/assets/documents/catalogue-des-outils-pedagogiques-jeunesse.pdf">
https://www.ademe.fr/sites/default/files/assets/documents/catalogue-des-outils-pedagogiques-jeunesse.pdf
</a>
', '<p>Sont ciblés dans cette action les établissements scolaires du cycle primaire (école maternelle et élémentaire) et dans une moindre mesure secondaire (collège, lycée). Sont également concernés les structures d’accueil collectif des jeunes enfants, à savoir : établissement multi-accueil, crèche parentale, jardin d’enfants, micro-crèche, halte-garderie... Les maisons d’assistant(e)s maternels et les relais d’assistant(e)s maternels sont également concernés s’il en existe sur le territoire.</p>
<p>Labellisation unique ville-EPCI : Lorsque la compétence sur les écoles est communale, évaluer la ville sur 3 points et évaluer sur un point ce que fait l''EPCI en soutien sur les autres communes du territoire en base (10%) et mise en œuvre (15%).</p>
<p>Ne pas évaluer :</p>
<ul>
<li>
<p>projets datant de plus de 4 ans</p>
</li>
<li>
<p>les partenariats avec les établissements d''enseignement supérieur et la recherche (action 6.3.3)</p>
</li>
<li>
<p>les aspects liés à la restauration collective (action 6.4.1)</p>
</li>
</ul>
<p>Liens vers d’autres actions :</p>
<ul>
<li>
<p>6.1.1 : Développer une stratégie partenariale multi-niveaux</p>
</li>
<li>
<p>4.1.1 : Soutenir, promouvoir et suivre les pratiques multimodales sur le territoire</p>
</li>
<li>
<p>6.1.3 : Développer un plan de communication pour chaque cible du territoire</p>
</li>
<li>
<p>6.5.2 : Inciter les citoyens à une consommation responsable, limiter l’emprise de la publicité et mobiliser les relais d’opinion</p>
</li>
</ul>
', '<p>Moins 50% si la collectivité n’est pas en charge des écoles (le reste du potentiel est maintenu pour la compétence « soutien aux actions de maîtrise de la demande d''énergie » qu’elle peut prendre de manière facultative).</p>
<p>Moins 100% s’il n’y a aucun établissement scolaire ou structure d’accueil de jeunes enfants sur le territoire.</p>
', 4.0, null, null);
