insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.5.3.5.3', 'cae', '6.5.3.5.3', 'Contrôler l’efficacité des actions mises en place', '<p>Par exemple : baisse constatée des consommations d’économies d''énergie ou d’eau réalisées, baisse de la quantité de déchets résiduels…</p>
