insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.2.1.1', 'cae', '3.1.2.1.1', 'S''engager à mener une politique active de promotion des achats d''électricité verte auprès de ses habitants et acteurs socio-économiques pour augmenter la demande', '<p>Doit se faire à côté d''un engagement en faveur de la production d''électricité verte ou de l’auto-consommation sur le territoire.</p>
