insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.2.1.2', 'cae', '3.1.2.1.2', 'Faire la promotion et informer les habitants et les entreprises de la possibilité d''acheter de l''électricité verte et sur les critères de qualité à respecter', '<p>Exemples de critères de qualité: existence de labels au niveau européen, 100% ENR, qualité environnementale, transparence, critère d''additionalité, fond de développement des ENR...</p>
', '', '', '', '', '', null, null, null);
