insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.2.2.1', 'cae', '3.1.2.2.1', 'Faire apparaitre de manière évidente dans les objectifs politiques des fournisseurs locaux (factures) des principes de tarification des agents énergétiques favorisant un comportement économe en énergie', '<p>Faire apparaître ces principes ou a minima la limitation des pointes de consommation: prix différenciés selon les saisons (été/hiver), différenciation heure pleine/heure creuse, pas de réduction liée à la quantité, modèles tarifaires novateurs (par ex. tarif d''efficacité)…</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.2.2.2', 'cae', '3.1.2.2.2', 'Indiquer une valeur de référence (moyenne nationale ou régionale d''un ménage-type), l''évolution des consommations par rapport aux années précédentes, des recommandations ou des sites-ressources sur les factures d''électricité, de gaz ou chauffage urbain', '', '', '', '', '', '', null, null, null);
