insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.2.3.1', 'cae', '3.1.2.3.1', 'Bénéficier et/ou solliciter son syndicat pour mettre en place des services en matière de MDE et d''ENR', '<p>Le syndicat d’énergie auquel adhère la collectivité propose des services en matière de MDE et d’ENR aux collectivités (groupement de commande d’achats d’énergie renouvelable, récupération de CEE, études d’opportunité ENR, CEP…) ;</p>
', '', '', '', '', '', null, null, null);
