insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.2.3.3', 'cae', '3.1.2.3.3', 'Se fixer des objectifs de performances et d’amélioration de ces services', '<p>Par exemple: certification ISO, labellisation Clair’énergie ou référence à des critères de qualité inspirés de démarche de labellisation de l’électricité européennes comme OK Power en Allemagne ou Naturmade Star en Suisse –respect de l’environnement des installations de productions, additionnalité...</p>
', '', '', '', '', '', null, null, null);
