insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.1.1', 'cae', '3.1.1.1.1', 'Connaître les enjeux de la distribution d’énergie sur son territoire', '<p>La collectivité a une bonne connaissance de son rôle (et de celui du syndicat compétent le cas échéant), des contrats de concession, du compte-rendu d’activité du concessionnaire (rapport CRAC : Compte-rendu Annuel de l’Activité du Concessionnaire).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.1.2', 'cae', '3.1.1.1.2', 'Demander au syndicat d’énergie ou au concessionnaire/gestionnaire du réseau les informations utiles à sa politique énergie', '<p>Informations utiles: consommations totales par commune, le nb de points de livraison (en soutirage et en injection), le nb de producteurs et la puissance installée et raccordée par énergie, nb et identité des fournisseurs actifs sur le territoire et volume vendu par ces fournisseurs, les dates d''échéance des contrats de concession...</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.1.3', 'cae', '3.1.1.1.3', 'Suivre les concessions par des personnes dédiées et spécialisées', '<p>Ces personnes sont formées notamment aux Smart Grids - au sens large, tous réseaux confondus) : syndicat d’énergie en cas de délégation de la compétence, ou désignation d’une personne en interne, ou mission d’un AMO pour renforcer les compétences.</p>
', '', '', '', '', '', null, null, null);
