insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.3', 'cae', '3.1.1.3', 'Intégrer des critères d’efficacité énergétique et de développement des énergies renouvelable dans le service public de distribution d’énergie', '', '', '', '', '', '', null, 30.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.3.1', 'cae', '3.1.1.3.1', 'Pour les réseaux de chauffage/froid urbain, être attentif à la performance environnementale dans le choix de son concessionnaire et inscrire des exigences dans le contrat et les avenants qui les lient', '<p>Exemple d’exigences : réalisation des travaux et conseils en MDE auprès de la collectivité et des usagers, études de potentiel et promotion des énergies renouvelables, mise en place d’un fond dédié à l’amélioration de performance énergétique du réseau, système de management environnemental type ISO, suivi d’un indicateur en gCO2/kWh... La réalisation effective des actions est vérifiée via le rapport annuel du concessionnaire. Des indicateurs de performance énergétique, environnementale et économique du réseau de chaleur sont mis en évidence à travers un suivi mensuel ou a minima un bilan annuel.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.3.2', 'cae', '3.1.1.3.2', 'Demander aux concessionnaires de préciser dans leurs rapports annuels les mesures d''amélioration des performances énergétiques réalisées et l''état des réflexions et actions sur les smart grid ou l’injection du bio-méthane dans le réseau gaz', '<p>Exemples de mesures d''amélioration des performances énergétiques: transformateurs performants, calorifugeage des réseaux de chaleur, mesures de rendement...</p>
<p>Exemples d''état sur les smart grid: état du déploiement des compteurs Linky et Gazpar, utilisation des données issues des compteurs communicants, opportunités et besoins de flexibilité, réflexion sur l''intégration de productions EnR électriques décentralisées, réflexion sur l''intégration des véhicules électriques et ses répercussions sur les réseaux...</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.3.3', 'cae', '3.1.1.3.3', 'En régie, afficher et formaliser la performance énergétique du service et sa contribution à la politique MDE et EnR de la collectivité', '<p>(règlement de service, délibération, service dédié). Les données sont capitalisées et partagées en interne pour suivre les effets de la politique énergie-climat.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.4', 'cae', '3.1.1.4', 'Définir et piloter une stratégie en matière d’optimisation des réseaux pour dialoguer plus efficacement avec les acteurs spécialisés', '', '', '', '', '', '', null, 20.0, 'mise en œuvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.4.1', 'cae', '3.1.1.4.1', 'Définir un plan stratégique / une feuille de route propre à la collectivité', '<p>Il doit être alimenté par les rapports annuels des concessionnaires, suivi dans le temps par des agents formés. Les solutions organisationnelles et techniques permettant le soutien au développement des énergies renouvelables électriques sur le territoire en minimisant les couts de raccordement pour les producteurs sont par exemple étudiées.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.4.2', 'cae', '3.1.1.4.2', 'Suivre et mettre à jour le schéma de cohérence de développement des réseaux de distribution d’énergie du PCAET', '<p>Le suivi et la mise à jour doit être réalisé par la collectivité et ses gestionnaires. Il est actualisé avec les opérations d’aménagement de la collectivité et le développement des énergies renouvelables sur le territoire.</p>
', '', '', '', '', '', null, null, null);
