insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.5.1', 'cae', '3.1.1.5.1', 'Oeuvrer (via l’autorité concédante le cas échéant) pour la mise en place d''un service de flexibilité local auprès du gestionnaire de réseau (étude en cours ou service en place), appelé également « boucle énergétique locale » ou équivalent', '<p>Montage d’une action pilote de MDE sur une zone particulière, en coordonnant tous les intervenants, en premier lieu le Syndicat d’Energie et le gestionnaire de réseau électrique.</p>
<p>Exemple de contenu : données de consommation des bâtiments (agrégées à différentes échelles : IRIS, rue, …)  et réseaux fournies par les gestionnaires des réseaux, croisement des données énergie avec les données du parc de bâtiments via SIG pour identifier les zones d''actions prioritaires et définir des indicateurs de suivi pertinents, conseils études et incitations grand-public, actions commerciales des fournisseurs orientées MDE grâce aux CEE, actions ciblées sur les zones où le réseau est en contrainte, montage de dossiers FACE de type macro…</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.5.2', 'cae', '3.1.1.5.2', 'Accompagner des projet pilotes en auto-consommation d’électricité renouvelable pour augmenter les taux d’auto-consommation et d’auto-production (juste dimensionnement, synchronisation de la consommation avec la production, opération collective…)', '', '', '', '', '', '', null, null, null);
