insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.4.4.1', 'cae', '3.3.4.4.1', 'Pratiquer la gestion différenciée des espaces verts et systématiser les pratiques alternatives aux produits phytosanitaires', '<p>Cela limite au global les consommations d''énergie liées à l''entretien, les quantités d''engrais (notamment chimiques) et d''eau dans l''objectif &quot;zéro phyto&quot; obligatoire depuis 2017.</p>
