insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.4.6', 'cae', '3.3.4.6', 'Mettre en place des actions de préservation de la biodiversité contribuant à l’adaptation au changement climatique et à l’amélioration du cadre de vie', '<p>Les actions ci-dessous favorisent la biodiversité et permettent, dans une approche intégrée, de diminuer le risque inondation, de lutter contre le phénomène d’îlot de chaleur urbain et de développer la “Nature en Ville”.</p>
