insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.4.7', 'cae', '3.3.4.7', 'Utiliser des outils de contractualisation opérationnels pour la mise en œuvre de la trame verte et bleue', '<p>La collectivité met en place/promeut des outils de contractualisation et de maîtrise foncière transversaux dans lesquels la &quot;préservation de la biodiversité, la protection des milieux et des ressources&quot; constitue un pilier majeur.</p>
