insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.2.4', 'cae', '3.3.5.2.4', 'Optimiser le captage du biogaz issu des centres d’enfouissement', '<p>par exemple : diminution des émissions en phase d’exploitation avant mise en œuvre des dispositifs définitifs de captage (captage précoce, optimisation de la durée d’exploitation des alvéoles ou casiers) et limitation des fuites aux parois du réseau, notamment par l’optimisation des dimensionnements des réseaux</p>
', '', '', '', '', '', null, null, null);
