insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.2.6', 'cae', '3.3.5.2.6', 'Mettre en place une gestion intégrée des différents biodéchets du territoire, avec une part de valorisation énergétique (méthanisation, combustion ou agro-carburants)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.2.7', 'cae', '3.3.5.2.7', 'Promouvoir les bonnes pratiques de compostage auprès des particuliers', '<p>Pour les collectivités pratiquant prioritairement le compostage, elles veillent : promouvoir les bonnes pratiques auprès des particuliers (formation de maîtres composteurs, distribution de fourche ou brasse-compost, aires de démonstration, rappel des paramètres clés : équilibre carbone/Azote, taux d''humidité et aération). Pour les plateformes de compostage centralisé, les meilleures pratiques sont réalisées et formalisées (courbes de suivi des températures et de l''humidité, aération...), l''impact des déplacements engendrés pour l''acheminement des déchets verts est pris en compte (choix de la localisation...) et le compost produit est valorisé localement et est conforme à la norme (analyses physico-chimiques). En milieu rural, du co-compostage à la ferme avec un ou plusieurs agriculteurs volontaires est réalisé.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.2.8', 'cae', '3.3.5.2.8', 'Contrôler l''interdiction du brûlage à l''air libre des déchets verts et mener de nombreuses actions sur le sujet', '<p>Des actions de sensibilisation sont menées.</p>
<p>Des solutions alternatives sont proposées aux particuliers (amélioration de la collecte des déchets verts, travail sur le compostage dans la restauration collective, filière courte de valorisation pour alimenter les chaufferies bois...).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.2.9', 'cae', '3.3.5.2.9', 'Trouver des débouchés pour l''ensemble de l''énergie produite et pour le compost', '<p>par exemple : vente à un fournisseur de gaz, raccordement à un réseau de chaleur, favoriser l’installation d’utilisateurs d’énergie à proximité des sites de traitements des déchets producteurs d’énergie ), etc - pour l''énergie - en lien avec le secteur agricole - pour le compost -.</p>
', '', '', '', '', '', null, null, null);
