insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.1.2', 'cae', '3.3.2.1.2', 'Demander dans les contrats de DSP la recherche de l’efficacité énergétique des stations d''épuration ou l''inscrire dans la stratégie de la régie', '<p>Une certification ISO 14001 et/ou 50001, etc. du service ou des installations est un plus.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.2', 'cae', '3.3.2.2', 'Analyser le potentiel de récupération de chaleur sur les eaux usées', '<p>Le potentiel d''utilisation des calories  des stations d’épuration et des collecteurs d''eaux usées est déterminé, une étude existe. Elle doit croiser les tailles de collecteurs, les débits disponibles avec les possibilités d’utilisation (les sites consommateurs sont identifiés).</p>
', '', '', '', '', '', null, 10.0, 'bases');
