insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.3.1', 'cae', '3.3.2.3.1', 'Réaliser une étude sur les possibilités de valorisation des boues', '<p>La valorisation matière et la valorisation énergétique doivent être étudiées de manière complémentaire selon les débouchés et les possibilités techniques. Le co-traitement avec les déchets ménagers et/ou d’origine agricole est à étudier pour de faibles volumes.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.3.2', 'cae', '3.3.2.3.2', 'Etre attentif à l''actualisation des données et conclusions de l''étude sur les possibilités de valorisation des boues', '<p>Les gisements et techniques pouvant évoluer au fil du temps, la collectivité doit être attentive (attention aux études trop anciennes).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.4', 'cae', '3.3.2.4', 'Réaliser les travaux d’amélioration de l’efficacité énergétique du système d’assainissement', '<p>Exemples d''actions possibles pour améliorer l’efficacité énergétique sur les réseaux :</p>
<ul>
<li>
<p>réseaux séparatifs (hors centres villes anciens),</p>
</li>
<li>
<p>campagnes de vérification des conformités des branchements et de diminution des eaux claires parasites</p>
</li>
<li>
<p>campagnes de connaissance des états des collecteurs visitables et non visitables et programmation de travaux</p>
</li>
<li>
<p>optimisation des postes de relèvement (conditions hydrauliques, fonctionnement)</p>
</li>
<li>
<p>systèmes de stockage et dépollution des eaux pluviales (évalué dans la mesure 3.3.3 dédiée)</p>
</li>
</ul>
<p>Exemples d’action possibles pour améliorer l’efficacité énergétique des stations d’épuration :</p>
<ul>
<li>
<p>remplacement / optimisation du réseau d''aération des traitements biologiques, et mise en place d’une régulation  asservie à la charge à traiter (et non cadence - durée ou potentiel REDOX),</p>
</li>
<li>
<p>mise en place de variateurs de vitesse selon pertinence</p>
</li>
<li>
<p>Filière pluviale dédiée visant à limiter la dilution de l’effluent</p>
</li>
</ul>
<p>Nota : notation variable selon le % des stations d’épuration couvertes par des mesures d’optimisation énergétique (à moduler aussi en fonction du volume d’eau traité par ces stations).</p>
', '', '', '', '', '', null, 20.0, 'mise en œuvre');
