insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.5.2', 'cae', '3.3.2.5.2', 'Mettre en place un dispositif de valorisation des boues d’épuration et dépasser le simple respect de la règlementation dans sa mise en œuvre', '<p>Exemplees: méthanisation avec injection du biogaz dans le réseau ou cogénération si débouchés de la chaleur existantes à proximité (le chauffage des digesteurs et des locaux ne suffit pas, il est possible d’utiliser la chaleur de l’eau épurée pour cela), valorisation matière avec optimisation de la solution choisie ou caractère innovant montrant une réflexion poussée de la collectivité (séchage solaire des boues, traitements visant à améliorer l''intérêt agronomique des boues ou leur manutention, compostage… la réalisation du plan d’épandage règlementaire est apprécié mais ne suffit pas pour avoir l’ensemble des points).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.6', 'cae', '3.3.2.6', 'Suivre régulièrement les actions et évaluer leurs effets', '<p>Par défaut : ratio kWh/m3 épuré en diminution (comparaison sur plusieurs années).</p>
<p>Ces indicateurs sont régulièrement utilisés pour des actions correctives et d''amélioration.</p>
<p>Nota : 20% des points sur le premier critère, 5% sur les autres.</p>
', '', '', '', '', '', null, 30.0, 'effets');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.6.1', 'cae', '3.3.2.6.1', 'Suivre l''effet des mesures pour améliorer l’efficacité énergétique des systèmes d’assainissement par des indicateurs', '<p>Indicateurs prioritaires :</p>
<ul>
<li>
<p>ratio kWh/kgDBO5 éliminé se situe, selon filière, autour des valeurs suivantes : boues activées entre 2 et 4</p>
</li>
<li>
<p>SBR (réacteur biologique séquencé) autour de 4</p>
</li>
<li>
<p>BRM (bioréacteur à membranes) autour de 5.</p>
</li>
</ul>
', '', '', '', '', '', null, null, null);
