insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.3.5.1', 'cae', '3.3.3.5.1', 'Participer activement à l''élaboration d''une politique de gestion des inondations', '<p>Seulement si la collectivité est concernée, notamment dans la perspective de l''adaptation au changement climatique (échelle communale ou supra-communale, par exemple via les programmes d''actions de prévention contre les inondations (PAPI), le plan submersions rapides...).</p>
