insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.3.5.2', 'cae', '3.3.3.5.2', 'Mettre en place un centre de contrôle des installations d’assainissement et pluvial', '<p>La collectivité doit assurer la dépollution des rejets urbains par temps de pluie (temps de retour au moins mensuel) avant rejet au milieu naturel par la gestion dynamique des bassins et réseaux, la concentration des polluants et le traitement sur stations d’épuration.</p>
', '', '', '', '', '', null, null, null);
