insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.3.6.1', 'cae', '3.3.3.6.1', 'Avoir mis en place sur le territoire de nombreux ouvrages de gestion alternative des eaux pluviales', '<p>Leur mise en œuvre est systématiquement étudiée dans les projets : noues, tranchées, jardins de pluie, bassins paysagers, espaces inondables intégrés à l''aménagement...</p>
', '', '', '', '', '', null, null, null);
