insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.2.3', 'cae', '4.1.1.2.3', 'Travailler a minima avec les autres collectivités et les Autorités Organisatrices des Mobilités afin de développer l’intermodalité (promotion des autres modes de transports disponibles au départ ou à destination, etc.)', '<p>Les AOM sont compétentes en matière d’organisation des déplacements sur leur territoire.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.3', 'cae', '4.1.1.3', 'Promouvoir l’intermodalité et les modes de transports alternatifs à la voiture individuelle sous des formes et vers des cibles variées', '<p>La collectivité utilise pour cela des supports et des formats variés :</p>
<ul>
<li>
<p>support d''informations papier (plan, affiche, presse) et internet (plateforme de co-voiturage, calculateur d’itinéraires, …);</p>
</li>
<li>
<p>organisation d''évènements ayant pour sujet l''éco-mobilité (journée sans voiture, challenge de la mobilité,  animations festives autour du vélo, essai de vélos à assistance électrique, « vélorution »...);</p>
</li>
<li>
<p>livret d’information, plan thématique et « offres découvertes » pour les nouveaux arrivants;</p>
</li>
<li>
<p>conseils en mobilité lors des nouvelles zones d''aménagement urbain ou de nouvelles constructions;</p>
</li>
<li>
<p>soutiens financiers variés;</p>
</li>
<li>
<p>mise en oeuvre d’aménagements urbains temporaires, en utilisant les principes de concertation de l’urbanisme tactique, pour tester de nouveaux aménagements encourageants les modes alternatifs en vue de les pérenniser si nécessaire.</p>
</li>
</ul>
<p>Les actions spécifiques à destination des scolaires sont évaluées dans la mesure 6.5.3.</p>
', '', '', '', '', '', null, 15.0, 'mise en œuvre');
